children.clientScript:
function openTab() {
	var SYSPARM_TRANSACTION_SCOPE = "sysparm_transaction_scope";
	function getUrlVars() {
		var vars = {};
		var parts = window.location.href.replace(/[?&]+([^=&]+)=([^&]*)/gi, function(m,key,value) {
			vars[key] = value;
		});
		return vars;
	}

	var scope = SYSPARM_TRANSACTION_SCOPE + '=' + getUrlVars()[SYSPARM_TRANSACTION_SCOPE];

	g_studio.openTab({
		title: 'Applications',
		subTitle: 'Application List',
		name: 'Applications',
		url: '$sg-studio.do?' + scope + '&sysparm_nostack=true#/',
		type: 'url'
	});
}

